import React from 'react'; 
import { useState } from 'react'; 



export default function Create() {

const [newRecipe, setNewRecipe] = useState({
    name: '',
    summary: '',
    healthScore: '',
    stepByStep: '',
    diets: []
}); 

const [errors, setErrors] = useState({

})

const handleInputEvent = (event) => {
    setNewRecipe({
        ...newRecipe,
        [event.target.name]: event.target.value 
    });

    setErrors(validate({
        ...newRecipe,
        [event.target.name]: event.target.value
    }));

    console.log(errors)
}


    return (
        <div>
            <form>
                <h2>Your new Recipe: </h2>
                <label>Name</label>
                <input type='text' name='name' value={newRecipe.name} onChange={handleInputEvent}/>
                <label>Summary</label>
                <input type='text' name='summary' value={newRecipe.summary} onChange={handleInputEvent}/>
                <label>HealthScore</label>
                <input type='text' name='healthScore' value={newRecipe.healthScore} onChange={handleInputEvent}/>
                <label>Step by Step</label>
                <input type='text' name='stepByStep' value={newRecipe.stepByStep} onChange={handleInputEvent}/>
                <label>Diets</label>
                <input name='diets' value={newRecipe.diets} onChange={handleInputEvent}/>
                <input type='submit' name='Submit'/>
            </form>
        </div>
    )
}

export function validate(newRecipe) {
    let error = {}; 

    if(!newRecipe.name) {
        error.name = 'Recipe name can not be empty'
    }
    if(!newRecipe.summary) {
        error.summary = 'Recipe summary can not be empty'
    }
    if((!/(?=.*[0-100])/.test(newRecipe.healthScore))){
        error.healthScore = 'HealthScore must be a numer between 0 and 100'
    }
    return error; 
}
