                </div>

                <div>{ location.pathname.includes('/home') ? 'üç¥' : false }</div>

                <div>{ location.pathname.includes('/home') ? <button className='detailbutton'><Link to='/details'>Details</Link></button> : 'üè†' } 
            </div>

        </div>
    )
}
