import React from 'react'; 
import { useState } from 'react';
import { useDispatch } from 'react-redux';
import { getBackendDiets } from '../../Redux/actions';



export default function FilterDiet () {

    const dispatch = useDispatch()
    const [diets, setDiets] = useState('')

    function handleChange(event){
        event.preventDefault()
        setDiets(event.target.value)
    }

    function handleSubmit(event){
        event.preventDefault()
        dispatch(getBackendDiets())
    } 

    return (
        <div>
            <label>Filter by Diet</label>
            <input list='browser' onClick={event => handleSubmit(event)}></input>
            <button onClick={event => handleChange(event)}>Filter</button>
            <datalist>
                {diets?.map( d => <option>{ d.name }</option>)}
            </datalist>

        </div>
    )
}
