import React from 'react'; 
import { useDispatch } from 'react-redux';
import { Link } from 'react-router-dom';
import { getRecipeDetail } from '../../Redux/actions';
//destructuring of components

export default function SingleRecipe(props) {
    const dispatch = useDispatch()
    console.log(props.id)

    function handleClick(e) {
        dispatch(getRecipeDetail(props.id))
    }
    return (
        <div>
            <div>
               <div> 
                    <Link to='/recipeDetail' onClick = {(e)=> handleClick(props.id)}>{props.title}</Link> 
                </div> 
                <div> 
                    <h1>{props.summary}</h1> 
                </div> 
                <div> 
                    <h1>{props.healthScore}</h1> 
                </div> 
                <div> 
                    <h4>{props.diets}</h4> 
                </div> 
                <div> 
                    <h1>{props.instructions}</h1> 
                </div> 
               <div> 
                    <img src={props.image} alt='Imagen no disponible'/> </div>          
                </div>

                <div>{props.id}</div>

        </div>
    )
}
