import React from 'react'; 
import { useState } from 'react'; 
import { useDispatch } from 'react-redux';
import { postRecipe } from '../../Redux/actions';

export default function Create() {

const dispatch = useDispatch(); 

const [newRecipe, setNewRecipe] = useState({
    name: '',
    summary: '',
    healthScore: '', 
    stepByStep: '',
    diets: []
}); 

const [errors, setErrors] = useState({})

const handleInputEvent = (event) => {
    setNewRecipe({
        ...newRecipe,
        [event.target.name]: event.target.value 
    });

    setErrors(validate({
        ...newRecipe,
        [event.target.name]: event.target.value
    }));

    console.log(errors)
}

const handleSubmit = (event) => {
    event.preventDefault();
    dispatch(postRecipe(newRecipe))
    alert('Reecipe Created')
    setNewRecipe({
        name: '',
        summary: '',
        healthScore: '', 
        stepByStep: '',
        diets: []
    });
}



    return (
        <div>
            <form>
                <h2>Your new Recipe: </h2>
                <form onSubmit = {(event) => handleSubmit(event)}>
                <label>Name</label>
                <input type='text' name='name' value={newRecipe.name} 
                onChange={handleInputEvent}
                className={errors.name && 'danger'}/>
                {errors.name ? <p> <small>{errors.name}</small></p> : false}

                <label>Summary</label>
                <input type='text' name='summary' value={newRecipe.summary} 
                onChange={handleInputEvent}
                className={errors.summary}/>
                {errors.summary ? <p> <small>{errors.summary}</small></p> : false}

                <label>HealthScore</label>
                <input type='number' name='healthScore' value={newRecipe.healthScore} 
                onChange={handleInputEvent}
                className = {errors.healthScore}/>
                {errors.healthScore ? <p> <small>{errors.healthScore}</small></p> : false}

                <label>Step by Step</label>
                <input type='text' name='stepByStep' value={newRecipe.stepByStep} onChange={handleInputEvent}/>
                <label>Diets</label>
                <input name='diets' value={newRecipe.diets} onChange={handleInputEvent}/>
                <input type='submit' name='Submit' />
                </form>
            </form>
        </div>
    )
}

//validating input function
export function validate(newRecipe) {
    let error = {}; 

    if(!newRecipe.name) {
        error.name = 'Recipe name can not be empty'
    }
    if(!newRecipe.summary) {
        error.summary = 'Recipe summary can not be empty'
    }

    return error; 
}
