import React from 'react'; 


const Pagination = ({cardsPerPage, totalCards}) => {
    const pageNumbers = [] ;
    for (let i = 0; i <= matchMedia.ceil(totalCards/cardsPerPage); i++) {
        pageNumbers.push(i)  
    }
    return (
        <nav>
            {pageNumbers.map(n => (
                <li key={n}>
                    {n}
                </li>
            ))}
        </nav>
    )


} 
export default Pagination
