import React from 'react'; 
import { Link } from 'react-router-dom';
import { useSelector, useEffect } from 'react-redux';
import { getRecipeDetail } from '../../Redux/actions';
//destructuring of components

export default function SingleRecipe(props) {

    const id = props.match.params.id

    useEffect(()=> {
        dispatchEvent(getRecipeDetail(id))
    })

    return (
        <div>
            <div>
               <div> 
                    <h3>{props.title}</h3> 
                </div> 
                <div> 
                    <h1>{props.summary}</h1> 
                </div> 
                <div> 
                    <h1>{props.healthScore}</h1> 
                </div> 
                <div> 
                    <h4>{props.diets}</h4> 
                </div> 
                <div> 
                    <h1>{props.instructions}</h1> 
                </div> 
               <div> 
                    <img src={props.image} alt='Imagen no disponible'/> </div>          
                </div>
                <div>{props.id}</div>
                <Link to='/recipe/{props.id}'> Click here for Recipe Detail </Link>
        </div>
    )
}
