import React from 'react'; 
import { useDispatch } from 'react-redux';
import { Link, useLocation } from 'react-router-dom';
import { getRecipeDetail } from '../../Redux/actions';

//destructuring of components

export default function SingleRecipe({title, summary, healthScore, diets, instructions, key, image}) {

    const dispatch = useDispatch();
    const location = useLocation(); 

    function handleClick(e){
        e.preventDefault(); 
        dispatch(getRecipeDetail(key))
    }

    return (
        <div>
            <div> 
                <h1>{title}</h1>

                <h1>{summary}</h1>

                <div> 
                    <h1>{healthScore}</h1> 
                </div> 
                <div> 
                    <h4>{diets}</h4> 
                </div> 
                <div> 
                    <h1>{instructions}</h1> 
                </div> 
               <div> 
                    <img src={image} alt='Imagen no disponible'/> </div>          
                </div>

                <div><h3>{key}</h3></div>
                { location.pathname.includes('/home') ? <button onClick={(e) => handleClick(e)}><Link to='/details'>Details</Link></button> : 'üè†' }

        </div>
    )
}
